import random
while True: print(uc:=input("Your choice:"), print(cc:=f"Computer choice: {random.choice(['Rock', 'Paper', 'Scissor'])}"), print(r:=f"{uc[0]}{cc[17]}".upper(), print("You win") if (r=="RS" or r=="PR" or r=="SP") else print("You Lose") if (r=="RP" or r=="PS" or r=="SR") else print("Draw") if (r=="RR" or r=="PP" or r=="SS") else print("Invalid Input"), end="\r"), end="\r")
